    /**
     * Store a newly created resource in storage.
     *
     * @param  \Illuminate\Http\Request  $request
     * @return \Illuminate\Http\Response
     */
    public function store(Request $request)
    {
      // dd($request->image);
      if ($request->dob == null || $request->dob == '') {
        Session::flash('status','Please enter a valid Date of Birth.');
        return Redirect::back();
      }
      $today = \Carbon\Carbon::now();
      $dobDate = \Carbon\Carbon::parse($request->dob);

      if ($dobDate->isToday() || $dobDate->gte($today)) {
        Session::flash('status','Date of birth can\'t be equal or greater than today');
        return Redirect::back();
      }
      if ($request->join_date != null && $request->join_date != '') {
        $dobDate = \Carbon\Carbon::parse($request->dob);
        $joinDate = \Carbon\Carbon::parse($request->join_date);
        $result = $dobDate->lessThan($joinDate);
        $today = \Carbon\Carbon::now();




        if (!$result) {
          Session::flash('status','Join date can\'t be greater than or equal to Date of birth.');
          return Redirect::back();
        }


      }
      if (!$request->hasFile('aimage')) {
        $image = '';
      }else{
        $image = $request->file('aimage')->store('image');
      }


      DB::table('participant')->insert(
          [
            'name' => $request->input('name'),
            'email' => $request->input('email'),
            'phone' => $request->input('phone'),
            'nick' => $request->input('nick'),
            'address' => $request->input('address'),
            'join_date' => $request->input('join_date'),
            'country'  => $request->country,
            'image' => $image,
            'color' => '0',
            'dob'=>$request->dob
          ]
      );
        // return "m awais";
      Session::flash('alert', 'alert alert-success');
      Session::flash('status', "Successfully Inserted!");
      return Redirect::to(route("participant.create"));

    }

