    /**
     * Update the specified resource in storage.
     *
     * @param  \Illuminate\Http\Request  $request
     * @param  int  $id
     * @return \Illuminate\Http\Response
     */
    public function update(Request $request, $id)
    {

      if ($request->hasFile('aimage')) {
        $image = $request->file('aimage')->store('image');
        DB::table('participant')->where('id','=',$id)->update([
          'image'=>$image
        ]);
      }
      DB::table('participant')
          ->where('id', $id)
          ->update([
            'name' => $request->input('name'),
            'email' => $request->input('email'),
            'phone' => $request->input('phone'),
            'nick' => $request->input('nick'),
            'address' => $request->input('address'),
            'country' => $request->country,
            'join_date' => $request->input('join_date'),
            'dob'=>$request->dob
          ]);
          return Redirect::to(route("participant.index"));
    }

    /**
     * Remove the specified resource from storage.
     *
     * @param  int  $id
     * @return \Illuminate\Http\Response
     */
    public function destroy($id)
    {
        $teamsMembers = DB::Table('teamsmembers')->where('pid',$id)->join('teams','teams.id','=','teamsmembers.tid')->get();
        if ($teamsMembers->count()) {
          return response()->json($teamsMembers);
        }
        $participant = DB::table('participant')->where('id','=',$id)->first();
        \Storage::delete($participant->image);
        DB::table('participant')->delete($id);
        return $id;
    }
